mapper.select:
select
id, file_id, page_no, page_path, create_time, update_time
from c_contract_file_page


        and id = #{id}


        and file_id = #{fileId}


        and page_no = #{pageNo}


        and page_path = #{pagePath}


        and create_time = #{createTime}


        and update_time = #{updateTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from c_contract_file_page


        and id = #{id}


        and file_id = #{fileId}


        and page_no = #{pageNo}


        and page_path = #{pagePath}


        and create_time = #{createTime}


        and update_time = #{updateTime}
