mapper.select:
select
id, user_id, status, origin_file_id, compare_file_id, create_time, update_time
from c_contract_record


        and id = #{id}


        and user_id = #{userId}


        and status = #{status}


        and origin_file_id = #{originFileId}


        and compare_file_id = #{compareFileId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from c_contract_record


        and id = #{id}


        and user_id = #{userId}


        and status = #{status}


        and origin_file_id = #{originFileId}


        and compare_file_id = #{compareFileId}


        and create_time = #{createTime}


        and update_time = #{updateTime}
