mapper.select:
select
id, contract_id, page_id, content, create_time, update_time
from c_contract_result


        and id = #{id}


        and contract_id = #{contractId}


        and page_id = #{pageId}


        and content = #{content}


        and create_time = #{createTime}


        and update_time = #{updateTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from c_contract_result


        and id = #{id}


        and contract_id = #{contractId}


        and page_id = #{pageId}


        and content = #{content}


        and create_time = #{createTime}


        and update_time = #{updateTime}
